import React from 'react';
import { User } from 'lucide-react';

const RecentCoursesCard = () => {
  const recentCourses = [
    {
      title: 'Operating System',
      subtitle: 'Best Rating',
      instructor: 'Jose Moui',
      image: 'https://images.unsplash.com/photo-1519389950473-47ba0277781c?auto=format&fit=facearea&w=96&h=96', // Example image
    },
    {
      title: 'C Programming',
      subtitle: 'Best Rating',
      instructor: 'Jose Moui',
      image: 'https://images.unsplash.com/photo-1503676382389-4809596d5290?auto=format&fit=facearea&w=96&h=96', // Example image
    },
  ];

  return (
    <div className="col-span-1 bg-white p-4 rounded-lg shadow-sm border border-gray-200">
      <h3 className="text-lg font-semibold mb-4">Recent Courses</h3>
      <p className="text-sm text-gray-500 mb-4">Courses you have recently accessed</p>
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {recentCourses.map((course, index) => (
          <div key={index} className="p-3 bg-gray-50 rounded flex items-center space-x-4">
            <img
              src={course.image}
              alt={course.title}
              className="w-12 h-12 rounded object-cover border"
            />
            <div>
              <h4 className="font-medium text-gray-900 text-sm">{course.title}</h4>
              <p className="text-xs text-gray-600">{course.subtitle}</p>
              <div className="flex items-center mt-2">
                <User size={12} className="text-gray-400 mr-1" />
                <span className="text-xs text-gray-600">{course.instructor}</span>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RecentCoursesCard;
