import React from 'react';

const DashboardStatsCards = () => {
  const statsCards = [
    { title: 'Students', value: '26', color: 'bg-white', textColor: 'text-gray-900' },
    { title: 'Teachers', value: '15', color: 'bg-white', textColor: 'text-gray-900' },
    { title: 'Admin', value: '5', color: 'bg-white', textColor: 'text-gray-900' },
    { title: 'Courses', value: '5', color: 'bg-white', textColor: 'text-gray-900' },
  ];

  return (
    <div className="grid grid-cols-4 gap-4 mb-6">
      {statsCards.map((card, index) => (
        <div key={index} className="bg-white p-4 rounded-lg shadow-sm border border-gray-200">
          <div className="text-center">
            <h3 className="text-sm font-medium text-gray-600 mb-1">{card.title}</h3>
            <p className="text-2xl font-bold text-gray-900">{card.value}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default DashboardStatsCards;
