import React, { useEffect, useState } from "react";
import { Shield, Users, Settings, FileText } from "lucide-react";
import Sidebar from "./Sidebar";
import DashboardHeader from "./DashboardHeader";
import { auth } from "../firebase";
import { useNavigate } from "react-router-dom";

const adminTools = [
	{
		icon: <Shield className="text-blue-600" size={28} />,
		title: "Manage Roles",
		description: "Assign or remove admin, teacher, and student roles.",
	},
	{
		icon: <Users className="text-green-600" size={28} />,
		title: "User Management",
		description: "View, edit, or remove users from the system.",
	},
	{
		icon: <Settings className="text-yellow-600" size={28} />,
		title: "System Settings",
		description: "Configure grading, notifications, and other settings.",
	},
	{
		icon: <FileText className="text-purple-600" size={28} />,
		title: "Reports",
		description: "Generate and download academic or attendance reports.",
	},
];

const AdminTools = () => {
	const navigate = useNavigate();
	const [user, setUser] = useState(auth.currentUser);

	useEffect(() => {
		const unsubscribe = auth.onAuthStateChanged((currentUser) => {
			if (!currentUser) {
				navigate("/");
			} else {
				setUser(currentUser);
			}
		});
		return () => unsubscribe();
	}, [navigate]);

	return (
		<div style={{ display: "flex" }} className="flex min-h-screen bg-gray-50">
			<Sidebar user={user} />
			<div className="flex-1 flex flex-col">
				<DashboardHeader user={user} title="Admin Tools" />
				<main className="flex-1 p-6 space-y-6">
					<div className="w-full max-w-4xl mx-auto bg-white rounded-xl shadow p-8">
						<h2 className="text-2xl font-bold mb-6 text-center">
							Admin Tools
						</h2>
						<div className="grid grid-cols-1 md:grid-cols-2 gap-6">
							{adminTools.map((tool, idx) => (
								<div
									key={idx}
									className="flex items-start bg-gray-100 rounded-lg p-5 hover:shadow transition-shadow"
								>
									<div className="mr-4">{tool.icon}</div>
									<div>
										<h3 className="font-semibold text-lg">{tool.title}</h3>
										<p className="text-gray-600 text-sm">
											{tool.description}
										</p>
									</div>
								</div>
							))}
						</div>
					</div>
				</main>
			</div>
		</div>
	);
};

export default AdminTools;
