import React from "react";
import { Shield, Users, Settings, FileText } from "lucide-react";

const adminTools = [
	{
		icon: <Shield className="text-blue-600" size={28} />,
		title: "Manage Roles",
		description: "Assign or remove admin, teacher, and student roles.",
	},
	{
		icon: <Users className="text-green-600" size={28} />,
		title: "User Management",
		description: "View, edit, or remove users from the system.",
	},
	{
		icon: <Settings className="text-yellow-600" size={28} />,
		title: "System Settings",
		description: "Configure grading, notifications, and other settings.",
	},
	{
		icon: <FileText className="text-purple-600" size={28} />,
		title: "Reports",
		description: "Generate and download academic or attendance reports.",
	},
];

const AdminTools = () => (
	<div className="min-h-screen bg-gray-50 flex flex-col items-center py-10">
		<div className="w-full max-w-4xl bg-white rounded-xl shadow p-8">
			<h2 className="text-2xl font-bold mb-6 text-center">Admin Tools</h2>
			<div className="grid grid-cols-1 md:grid-cols-2 gap-6">
				{adminTools.map((tool, idx) => (
					<div
						key={idx}
						className="flex items-start bg-gray-100 rounded-lg p-5 hover:shadow transition-shadow"
					>
						<div className="mr-4">{tool.icon}</div>
						<div>
							<h3 className="font-semibold text-lg">{tool.title}</h3>
							<p className="text-gray-600 text-sm">{tool.description}</p>
						</div>
					</div>
				))}
			</div>
		</div>
	</div>
);

export default AdminTools;
